func drawFleur(
	size string,
	width float64,
	height float64,
	foregroundColor string,
	backgroundColor string,
	lines []string,
	fontFamily *canvas.FontFamily,
) string {

	// Define color map
	colors := map[string]string{
		"black": "#000000",
		"white": "#FFFFFF",
		"green": "#013801",
		"tan":   "#e5c498",
		"brown": "#552a0b",
		"blue":  "#0000e4",
		"grey":  "#D6DAD2",
	}

	// Initialize SVG builder
	builder := svgutils.NewSVGBuilder(width, height)

	if size == "large" {
		drawLargeFleur(builder, colors[foregroundColor], colors[backgroundColor])
	} else if size == "medium" {
		drawMediumFleur(builder, colors[foregroundColor], colors[backgroundColor])
	} else {
		log.Fatalf("Invalid size: %s", size)
	}

	// draw text
	numLines := len(lines)

	if numLines > 0 {
		// Calculate the total height for the text containers
		const topPadding = 3.25
		const bottomPadding = 3.25
		lineSpacing := 0.5
		availableHeight := height - (topPadding + bottomPadding) - float64(numLines-1)*lineSpacing

		// determine dimensions for each line
		type ContainerDimensions struct {
			Width  float64
			Height float64
		}
		var containerDimensions []ContainerDimensions
		switch numLines {
		case 3:
			containerDimensions = []ContainerDimensions{
				{Height: availableHeight * 0.25, Width: width - 4.75},
				{Height: availableHeight * 0.5, Width: width - 3.0},
				{Height: availableHeight * 0.25, Width: width - 4.75},
			}
		case 2:
			containerDimensions = []ContainerDimensions{
				{Height: availableHeight * 0.6, Width: width - 4.25},
				{Height: availableHeight * 0.4, Width: width - 4.25},
			}
		}

		// Calculate the starting y position for the topmost line
		currentY := height - topPadding

		for i, line := range lines {
			// draw text container
			containerDimensions := containerDimensions[i]
			container := canvas.Rectangle(containerDimensions.Width, containerDimensions.Height)
			containerBounds := container.Bounds()
			containerX := width/2 - containerBounds.W()/2
			containerY := currentY - containerDimensions.Height

			// Position the container
			container = container.Translate(containerX, containerY)
			container = container.Scale(1, -1)
			container = container.Translate(0, height)

			builder.AddPath(container.ToSVG(), map[string]string{
				"fill": "none",
				// uncomment to see container
				"stroke":       "pink",
				"stroke-width": "0.025",
				"id":           fmt.Sprintf("text-container-%d", i),
			})

			// Draw text
			fontSize := 20.0
			face := fontFamily.Face(fontSize, canvas.FontBold, canvas.FontNormal)
			textPath, _, err := face.ToPath(line)
			if err != nil {
				log.Fatalf("Failed to convert text to path: %s", err)
			}
			textBounds := textPath.FastBounds()

			// Scale the text to fit within the container
			scale := min(containerBounds.W()/textBounds.W(), containerBounds.H()/textBounds.H())
			textPath = textPath.Scale(scale, scale)

			// Recalculate text position after scaling
			textBounds = textPath.FastBounds()
			// Add a small offset to shift text left
			offset := 0.5
			x := containerX + (containerBounds.W()-textBounds.W())/2 - offset*scale
			y := containerY + (containerBounds.H()-textBounds.H())/2
			textPath = textPath.Translate(x, y)
			textPath = textPath.Scale(1, -1)
			textPath = textPath.Translate(0, height)

			builder.AddPath(textPath.ToSVG(), map[string]string{
				"fill": colors[backgroundColor],
				"id":   fmt.Sprintf("text-line-%d", i),
			})

			// Update currentY for the next line
			currentY -= containerDimensions.Height + lineSpacing
		}
	}

	builder.EndGroup()

	return builder.Close()
}
