func drawSvg(
	ctx *canvas.Context,
	productConfig ProductConfig,
	foregroundColor color.RGBA,
	backgroundColor color.RGBA,
	lines []string,
	fontFamily *canvas.FontFamily,
) {

	if productConfig.ProductId == os.Getenv("ELLIPSE_PRODUCT_ID") {
		drawEllipse(ctx, productConfig.Width, productConfig.Height, foregroundColor, backgroundColor)
	} else if productConfig.ProductId == os.Getenv("RECTANGLE_PRODUCT_ID") {
		rectPath := canvas.RoundedRectangle(productConfig.Width, productConfig.Height, 0.25)
		rectPath = rectPath.Translate(0, 0)
		ctx.DrawPath(0, 0, rectPath)
	} else if productConfig.ProductId == os.Getenv("DECO_PRODUCT_ID") {
		drawDeco(ctx, productConfig.Width, productConfig.Height, foregroundColor, backgroundColor)
	} else if productConfig.ProductId == os.Getenv("ALDER_PRODUCT_ID") {
		drawAlder(ctx, productConfig.Width, productConfig.Height, foregroundColor, backgroundColor, lines, fontFamily)
	} else if productConfig.ProductId == os.Getenv("FLEUR_PRODUCT_ID") {
		drawFleur(ctx, productConfig.Width, productConfig.Height, foregroundColor, backgroundColor, lines, fontFamily)
	} else if productConfig.ProductId == os.Getenv("CEZAR_PRODUCT_ID") {
		drawCezar(ctx, productConfig.Width, productConfig.Height, foregroundColor, backgroundColor, lines)
	} else if productConfig.ProductId == os.Getenv("RECURSO_PRODUCT_ID") {
		drawRecurso(ctx, productConfig.Width, productConfig.Height, foregroundColor, backgroundColor, lines)
	} else {
		panic("Invalid product ID: " + productConfig.ProductId)
	}
}
